create table QA
(
    ID       NUMBER default "TFUSER2"."SEQ_USER"."NEXTVAL" not null
        primary key,
    OPTION1  VARCHAR2(100),
    OPTION2  VARCHAR2(100),
    OPTION3  VARCHAR2(100),
    OPTION4  VARCHAR2(100),
    ANSWER   VARCHAR2(30)                                  not null,
    EXAM_ID  NUMBER
        constraint QA_EXAM__FK
            references EXAM,
    QUESTION VARCHAR2(300)                                 not null
)
/

